var Montage = require("montage/core/core").Montage,
    Component = require("montage/ui/component").Component;

exports.Main = Montage.create(Component, {

    feedURL: {
        value: 'http://rss.cnn.com/rss/cnn_mostpopular.rss',
        distinct: true
    },
    
    mapCategory: {
        get: function() {
            return this._mapCategory;
        },
        set: function(value) {
            this._mapCategory = value;
            this.map.category = value;
        }
    },
    
    feedReader: {value: null},
    
    map: {value: null},
    
    mapCenter: {value: null},
    
    handleGoAction: {
        value: function(event) {
            event.preventDefault();
            this.feedReader.feedURL = this.feedURL;
            
        }
    },
    
    handleMapButtonAction: {
        value: function(event) {
            event.preventDefault();            
            this.map.center = this.mapCenter;            
        }
    },
    
    draw: {
        value: function() {
            if(this.mapCenter != null) {
                this.map.center = this.mapCenter;
            }
        }
    }
    
});
